import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Login from "./Login/Login"; // 로그인 컴포넌트 import
import Header from "./components/Header";
import mainLogo from "./images/main_logo.png";

function App() {
  const [message, setMessage] = useState("");

    useEffect(() => {
        fetch("http://localhost:8080/hello")  // <-- 여기!
            .then(res => res.text())
            .then(data => setMessage(data))
            .catch(err => {
                console.error("API 요청 실패:", err);
                setMessage("서버 연결 실패 😢");
            });
    }, []);

    //<h1>Spring에서 받은 메시지:</h1>
    //<p>{message}</p>

    // <Router>
    //     <Routes>
    //         <Route path="/" element={
    //             <div>
    //                 <h1>IoT 환경 관리 시스템</h1>
    //                 <p>원하는 기능을 선택하세요:</p>
    //
    //                 <Link to="/login">
    //                     <button>로그인</button>
    //                 </Link>
    //             </div>
    //         } />
    //         <Route path="/login" element={<Login />} />
    //     </Routes>
    // </Router>

  return (
      <div>
          <Router>
              <Header />
              <Routes>
                  <Route path="/login" element={<Login />} />
              </Routes>
          </Router>

          {/* 메인 화면 */}
          <section style={styles.main}>
              <img src={mainLogo} alt="학교 이미지" style={styles.logo} />
              <div style={styles.content}>
                  <div style={styles.box}>
                      <h3>실내 상황</h3>
                      <p>현재 실내 온도: 23도</p>
                      <p>현재 실내 습도: 43%</p>
                      <p>현재 실내 미세먼지: 24ug</p>
                  </div>
                  <div style={styles.box}>
                      <h3>실외 상황</h3>
                      <p>현재 실외 온도: 23도</p>
                      <p>현재 실외 습도: 43%</p>
                      <p>현재 실외 미세먼지: 24ug</p>
                  </div>
                  <div>
                      <p>로그인을 해주세요</p>
                      <button style={styles.loginMainButton}>로그인 하기</button>
                  </div>
              </div>
          </section>
      </div>
  );
}

export default App;
